function checkForData(){chrome.storage.sync.get("tallySync",function(l){tally=isNaN(l.tallySync)?0:l.tallySync,display.innerHTML=tally})}var tally,display=document.getElementById("tally"),plus=document.getElementById("plus"),clos=document.getElementById("close");checkForData(),plus.addEventListener("click",function(){tally++,display.innerHTML=tally,chrome.storage.sync.set({tallySync:tally},function(){})}),clos.addEventListener("click",function(){tally=0,display.innerHTML=tally,chrome.storage.sync.set({tallySync:tally},function(){})}),checkForData();